import { Component, OnInit } from '@angular/core';
import { ActivatedRoute, Router } from '@angular/router';
import { SplitterType } from 'igniteui-angular';
import ProductModel from 'src/app/models/product.model';
import { NotifyService } from 'src/app/services/notify.service';
import { ProductsService } from 'src/app/services/products.service';

@Component({
  selector: 'app-search-results',
  templateUrl: './search-results.component.html',
  styleUrls: ['./search-results.component.css']
})
export class SearchResultsComponent implements OnInit {

  public typeHorizontal = SplitterType.Horizontal;
  public products: ProductModel[];


  constructor(
    private myActivatedRoute: ActivatedRoute,
    private notify: NotifyService,
    // private myRouter: Router,
    private myProductsService: ProductsService) { }

  async ngOnInit() {
    // try {
    //   this.myActivatedRoute.params.subscribe(async params => {
    //     this.products = await this.myProductsService.getProductsBySearch(params.query);
    //   });
    // }
    // catch (err) {
    //   this.notify.error(err);
    // }

    try {
      this.myActivatedRoute.queryParams.subscribe(async params => {
        this.products = await this.myProductsService.getProductsBySearch(params.q);      
      });
    } catch (err) {
      this.notify.error(err);
    }
    if (!this.products) {
      this.products = undefined;
    }
  }


}
<app-search-product></app-search-product>

<igx-splitter class="splitter" style='height: 85vh;' [type]="typeHorizontal">
    <igx-splitter-pane size='23%'>
        <app-menu></app-menu>
    </igx-splitter-pane>
    <igx-splitter-pane size='77%' minSize='800px' maxSize='1570px'>
        <div>

            <!-- <app-please-wait *ngIf="!products"></app-please-wait> -->

            <a routerLink="/products" [ngClass]="{fix : !products}">All Products</a>

            <app-category-list [ngClass]="{fix2 : !products}"></app-category-list>

            <h3 *ngIf="!products">No products match this searh...</h3>

            <br/>
            <app-product-card *ngFor="let p of products" [product]="p"></app-product-card>

        </div>
    </igx-splitter-pane>
</igx-splitter>
