import { Component, OnInit } from '@angular/core';
import { SplitterType } from 'igniteui-angular';
import { Subscription } from 'rxjs';
import ProductModel from 'src/app/models/product.model';
import { NotifyService } from 'src/app/services/notify.service';
import { ProductsService } from 'src/app/services/products.service';

@Component({
  selector: 'app-admin',
  templateUrl: './admin.component.html',
  styleUrls: ['./admin.component.css']
})
export class AdminComponent implements OnInit {

  public typeHorizontal = SplitterType.Horizontal;

  public products: ProductModel[];
  public addRequest: boolean = false;
  public product = new ProductModel();

  private subscriptionAdd: Subscription;
  private subscriptionUpdate: Subscription;

  constructor(private myProductsService: ProductsService, private notify: NotifyService) {

    this.subscriptionAdd = this.myProductsService.getCartObservable().subscribe(product => {
      if (product.product.ProductID>0) {
          this.addRequest = false;
      } else {
        this.addRequest = !this.addRequest
          // clear messages when empty message received
          // this.messages = [];
      }
    });

   }

  async ngOnInit() {
      try {
          this.products = await this.myProductsService.getAllProducts();
      }
      catch (err) {
          this.notify.error(err);
      }
  }

  public add() {
    this.product.ProductID = 0;
    this.myProductsService.adminProductUpdate(this.product);
  }

}
<mat-drawer-container class="example-container">
    <mat-drawer #drawer mode="side" style="width:500px" opened>
        <button class="plus" mat-raised-button type="button" (click)="add()"> 
            <mat-icon>add</mat-icon>
        </button>
        <div>
            <app-update-product></app-update-product>
            <app-add-product *ngIf="addRequest"></app-add-product>
        </div>
    </mat-drawer>
    <mat-drawer-content>

        <button *ngIf="leftArrow" mat-raised-button (click)="drawer.toggle()" (click)="left()">
            <mat-icon>arrow_left</mat-icon>
        </button>
        <button *ngIf="rightArrow" mat-raised-button (click)="drawer.toggle()" (click)="right()"><mat-icon>arrow_right</mat-icon>
        </button>

        <div>

            <div class="bar">
                <app-search-product></app-search-product>

                <app-please-wait *ngIf="!products"></app-please-wait>

                <a routerLink="/products">All Products</a>

                <app-category-list (click)="ngOnInit()"></app-category-list>
            </div>

            <app-product-card *ngFor="let p of products" [product]="p"></app-product-card>

            <h3 *ngIf="!products">No products match this search...</h3>


        </div>
    </mat-drawer-content>
</mat-drawer-container>
