import { Component, OnInit } from '@angular/core';
import { ActivatedRoute, NavigationStart, Router } from '@angular/router';
import ProductModel from 'src/app/models/product.model';
import { NotifyService } from 'src/app/services/notify.service';
import { ProductsService } from 'src/app/services/products.service';

@Component({
  selector: 'app-products-per-category',
  templateUrl: './products-per-category.component.html',
  styleUrls: ['./products-per-category.component.css']
})
export class ProductsPerCategoryComponent implements OnInit {

  public products: ProductModel[];
  public leftArrow: boolean = true;
  public rightArrow: boolean = false;

    constructor( private myActivatedRoute: ActivatedRoute, private router: Router, private myProductsService: ProductsService, private notify: NotifyService) {    }

    async ngOnInit() {
        try {
          const id = +this.myActivatedRoute.snapshot.params.id; // Take a route parameter named id.
          this.products = await this.myProductsService.getProductsPerCategory(id);        }
        catch (err) {
            // this.notify.error(err);
        }   
    }

    public left() {
      this.leftArrow = false;
      this.rightArrow = true;
    }

    public right() {
        this.leftArrow = true;
        this.rightArrow = false;
    }



}
<mat-drawer-container class="example-container">
    <mat-drawer #drawer mode="side" style="width:500px" opened>
        <app-menu></app-menu>
    </mat-drawer>
    <mat-drawer-content>

        <button *ngIf="leftArrow" mat-raised-button (click)="drawer.toggle()" (click)="left()">
            <mat-icon>arrow_left</mat-icon>
        </button>
        <button *ngIf="rightArrow" mat-raised-button (click)="drawer.toggle()" (click)="right()"><mat-icon>arrow_right</mat-icon>
        </button>
        <div>
            <div class="bar">
                <app-search-product></app-search-product>

                <app-please-wait *ngIf="!products"></app-please-wait>

                <a routerLink="/products">All Products</a>

                <app-category-list (click)="ngOnInit()"></app-category-list>
            </div>        
            <app-product-card *ngFor="let p of products" [product]="p"></app-product-card>

        </div>
    </mat-drawer-content>
</mat-drawer-container>
